mapper.select:
SELECT CASE WHEN COUNT(1) > 0 THEN 1 ELSE 0 END
FROM MEMBER
WHERE MEMBER_LOGIN_ID = #{memberLoginId}

SELECT CASE WHEN COUNT(1) > 0 THEN 1 ELSE 0 END
FROM MEMBER
WHERE EMAIL = #{email}

SELECT CASE WHEN COUNT(1) > 0 THEN 1 ELSE 0 END
FROM MEMBER
WHERE
    EMAIL = #{email}
    AND MEMBER_ID != #{selfId}
