mapper.select:
SELECT s.SUSPEND_ID, s.MEMBERSHIP_ID, s.SUSPEND_START_DATE, s.SUSPEND_END_DATE, s.SUSPEND_REASON, s.CREATED_BY, s.CREATED_AT, s.UPDATED_BY, s.UPDATED_AT, m.EXTENDED_END_DATE,
mem.MEMBER_NAME, m.MEMBERSHIP_TYPE, ca.ADMIN_NAME AS CREATED_BY_NAME, ua.ADMIN_NAME AS UPDATED_BY_NAME,
m.START_DATE AS ORIGINAL_START_DATE, m.END_DATE AS ORIGINAL_END_DATE, m.PERIOD_DAYS FROM MEMBERSHIP_SUSPEND_HISTORY s JOIN MEMBERSHIP m ON s.MEMBERSHIP_ID = m.MEMBERSHIP_ID LEFT JOIN MEMBER mem ON m.MEMBER_ID = mem.MEMBER_ID JOIN ADMIN ca ON s.CREATED_BY = ca.ADMIN_ID LEFT JOIN ADMIN ua ON s.UPDATED_BY = ua.ADMIN_ID WHERE s.SUSPEND_ID = #{suspendId}
